mapper.select:
select userId
from user_tbl
where userEmail = #{userEmail}

select sellerId
from seller_tbl
where sellerEmail = #{sellerEmail}

select * from seller_tbl where sellerId = #{sellerId}
and sellerPw =
#{sellerPw}

select userNickname, userPhone, userEmail, userPostCode,
userAddress, userAddressDetail
from user_tbl where userCode =
#{userCode}

select count(*) as nicknameCount
from user_tbl
where
userNickname = #{nickname}
or userPhone = #{phone}
or userEmail =
#{email}

mapper.update:
UPDATE user_tbl
SET
userPw = #{userPw}
WHERE userId = #{userId}
